import axios from "axios";
import React, { useState, useRef, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import ReCAPTCHA from "react-google-recaptcha";
import "./Style.css";
function Login() {
  console.log(window.location.href);
  const [showPassword, setShowPassword] = useState(false);
  const [submit, setSubmit] = useState(false);
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    reCaptchaToken: "",
  });
  const reRef = useRef();
  const navigate = useNavigate();

  const loginSubmit = async (e) => {
    e.preventDefault();
    const token = await reRef.current.executeAsync();
    console.log(token);
    setFormData({ ...formData, reCaptchaToken: token });
    setSubmit(true);
  };

  useEffect(() => {
    if (submit && formData.reCaptchaToken != "") {
      axios
        .post("http://admin.liveexamcenter.in/api/auth/login", formData, {
          headers: { Accept: "application/json" },
        })
        .then((response) => {
          localStorage.setItem(
            "_activeUser",
            JSON.stringify(response.data.user)
          );
          navigate("/questions");
        })
        .catch((error) => {
          console.log(error);
        });
    }
  }, [submit]);

  console.log(formData);
  return (
    <div className="login">
      <form onSubmit={loginSubmit}>
        <div className="container-login">
          <h1>Login to your account</h1>
          <div className="login-userName">
            <label>
              <b>Username</b>
            </label>
            <input
              type="text"
              placeholder="Enter Username"
              style={{ display: "block", width: "100%" }}
              name="uname"
              onChange={(e) =>
                setFormData({ ...formData, email: e.target.value })
              }
              required
            />
          </div>
          <div className="login-userName">
            <label>
              <b>Password</b>
            </label>
            <div style={{ position: "relative" }}>
              {showPassword == false ? (
                <>
                  <input
                    type="password"
                    placeholder="Enter Password"
                    style={{ display: "block", width: "100%" }}
                    name="psw"
                    required
                    onChange={(e) =>
                      setFormData({ ...formData, password: e.target.value })
                    }
                  />
                  <button
                    type="button"
                    className="pass-btn"
                    onClick={() => setShowPassword(!showPassword)}
                  ></button>{" "}
                </>
              ) : (
                <>
                  <input
                    type="text"
                    placeholder="Enter Password"
                    style={{ display: "block", width: "100%" }}
                    name="psw"
                    required
                    onChange={(e) =>
                      setFormData({ ...formData, password: e.target.value })
                    }
                  />
                  <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                  ></button>
                </>
              )}
            </div>

            <div>
              <button
                type="button"
                className="option-btm-btn"
                style={{
                  backgroundColor: "white",
                  border: "none",
                  marginTop: "30px",
                }}
              >
                {" "}
                Forgot Password?
              </button>
              <ReCAPTCHA
                sitekey="6Ld3COIZAAAAAC3A_RbO1waRz6QhrhdObYOk7b_5"
                size="invisible"
                ref={reRef}
              />
              <button type="submit" className="btn btn-primary login-submit">
                Login
              </button>
            </div>
            <label
              style={{
                textAlign: "center",
                margin: "15px auto",
                display: "block",
              }}
            >
              OR
            </label>
            <button
              type="button"
              className="btn btn-light"
              style={{ display: "block", align: "center", margin: "auto" }}
            >
              <i className="fab fa-google"></i> Login with Google
            </button>
          </div>
        </div>
        {/* <div class="container" style="background-color:#f1f1f1">
                    <button type="button" class="cancelbtn">Cancel</button>
                    <span class="psw">Forgot <a href="#">password?</a></span>
                </div>   */}
      </form>
    </div>
  );
}

export default Login;
