import axios from 'axios'
import React, { useState ,useRef} from 'react'
import { Link, useNavigate } from 'react-router-dom'
import recaptcha from 'react-google-recaptcha/lib/recaptcha';
import './Style.css';
function Login() {
    const tempformData =
    {
        email: "",
        password: "",
        reCaptchaToken: "",
    }
    const [submit,setSubmit] = useState (false)
    const [formData, setFormData] = useState(tempformData)
    const reRef = useRef()
    const navigate = useNavigate();
    const tokenKey = ""  
    const loginSubmit = async() => {
        const token = await reRef.current.executeAsync();
        axios.post('http://admin.liveexamcenter.in/api/auth/login', formData, { headers: { authorization: tokenKey } })
            .then(response => {
                navigate("/")
            })
            .catch(error => {
                console.log(error);
            })
    }

    <recaptcha siteKey="6Ld3COIZAAAAAC3A_RbO1waRz6QhrhdObYOk7b_5"
    size="invisible"
    ref={reRef}
    />
    return (
        <div className='login'>
            <form onSubmit={loginSubmit}>
                 <div className="container-login">
                     <h1>Login to your account</h1>
                     <div className='login-userName'>
                    <label><b>Username</b></label>
                    <input type="text" placeholder="Enter Username" style={{display:"block",width:"100%"}} name="uname" onChange={(e)=>setFormData({...formData,email:e.target.value})} required /> 
                    </div>
                    <div className='login-userName'>
                    <label><b>Password</b></label>
                    <input type="text" placeholder="Enter Password"  style={{display:"block",width:"100%"}} name="psw" required /> 
                    </div>
                    <div>
                    <button type='button' className='option-btm-btn' style={{backgroundColor:"white",border:"none",marginTop:"30px"}} onChange={(e)=>setFormData({...formData,password:e.target.value})}> Forgot Password?</button>
                    <button type="submit" className='btn btn-primary login-submit'>Login</button>

                    </div>
                    <label style={{textAlign:"center",margin:"15px auto",display:"block"}}>OR</label>
                    <button type='button' className='btn btn-light' style={{display:"block", align:"center",margin:"auto"}}><i className='fab fa-google'></i> Login with Google</button>
                </div>

                 {/* <div class="container" style="background-color:#f1f1f1">
                    <button type="button" class="cancelbtn">Cancel</button>
                    <span class="psw">Forgot <a href="#">password?</a></span>
                </div>   */}
            </form>
        </div>
    )
}

export default Login
