import axios from 'axios'
import React from 'react'
import { Link, useNavigate } from 'react-router-dom'
import './Style.css';
function Login() {
    const navigate = useNavigate();
    const tokenKey = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI2MWRkMjgwYWU2ZDdkNzdjOGU0ZjY4ZjYiLCJfYWN0aXZlT3JnIjoiNjE5Y2U0YThlNTg2ODUxNDYxMGM4ZGE3IiwiaWF0IjoxNjQ0NDY5NjY4LCJleHAiOjE2NDQ1MTI4Njh9.xcE84Ciu1W-lKBVdhg00xxyVvVhynYwjOJ6kkaXZ7SA"

    const loginSubmit = () => {

        axios.post('http://admin.liveexamcenter.in/api/auth/login', formData, { headers: { authorization: tokenKey } })
            .then(response => {
                navigate("/")
            })
            .catch(error => {
                console.log(error);
            })
    }
    return (
        <div className='login'>
            <form onSubmit={loginSubmit}>
                 <div className="container-login">
                     <h1>Login to your account</h1>
                     <div className='login-userName'>
                    <label><b>Username</b></label>
                    <input type="text" placeholder="Enter Username" style={{display:"block",width:"100%"}} name="uname" required /> 
                    </div>
                    <div className='login-userName'>
                    <label><b>Password</b></label>
                    <input type="text" placeholder="Enter Password"  style={{display:"block",width:"100%"}} name="psw" required /> 
                    </div>
                    <div>
                    <button type='button' className='option-btm-btn' style={{backgroundColor:"white",border:"none",marginTop:"30px"}}> Forgot Password?</button>
                    <button type="submit" className='btn btn-primary login-submit'>Login</button>

                    </div>
                    <label style={{textAlign:"center",margin:"15px auto",display:"block"}}>OR</label>
                    <button type='button' className='btn btn-light' style={{display:"block", align:"center",margin:"auto"}}><i className='fab fa-google'></i> Login with Google</button>
                </div>

                 {/* <div class="container" style="background-color:#f1f1f1">
                    <button type="button" class="cancelbtn">Cancel</button>
                    <span class="psw">Forgot <a href="#">password?</a></span>
                </div>   */}
            </form>
        </div>
    )
}

export default Login
