import React, { useState, useEffect } from 'react'
import axios from 'axios';
function AddQuestions() {

    const [subAPI, setSubAPI] = useState([]);
    const [topicAPI, setTopicAPI] = useState([]);
    //Read API For subject
    useEffect(() => {
        axios.get(`http://admin.liveexamcenter.in/api/subjects?term=`, { headers: { authorization: "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI2MWRkMjgwYWU2ZDdkNzdjOGU0ZjY4ZjYiLCJfYWN0aXZlT3JnIjoiNjE5Y2U0YThlNTg2ODUxNDYxMGM4ZGE3IiwiaWF0IjoxNjQzMDg0NjUyLCJleHAiOjE2NDMxMjc4NTJ9.2vXOFdSRH0NYjb-DOVdXnnGk8Gp3PEzyyvLcQjPWmIY" } })
            .then(response => {
                console.log(response.data.result)
                setSubAPI(response.data.result);
            })
            .catch(error => {
                console.log(error);
            })
    }, []);

    //Read API for Topics
    useEffect(() => {

        axios.get(`http://admin.liveexamcenter.in/api/topics?page=1&limit=5&term=`, { headers: { authorization: "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI2MWRkMjgwYWU2ZDdkNzdjOGU0ZjY4ZjYiLCJfYWN0aXZlT3JnIjoiNjE5Y2U0YThlNTg2ODUxNDYxMGM4ZGE3IiwiaWF0IjoxNjQzMDg0NjUyLCJleHAiOjE2NDMxMjc4NTJ9.2vXOFdSRH0NYjb-DOVdXnnGk8Gp3PEzyyvLcQjPWmIY" } })
            .then((res) => {
                console.log(res.data.result)
                setTopicAPI(res.data.result)
            })
            .catch((err) => {
                console.log(err)
            })
        console.log("in  useEffect...")

    }, [])

    const arr=[1,2,3,4];
    return (
        <div>
            <div className='add-div'>
                <div className='add-header'>
                    <h3>Add question</h3>
                </div>
                <form >
                    {/* middle part of add question page */}
                    <div className='add-main'>
                        {/* for select subject and topic */}
                        <div className='add-main-row'>
                            {/* for subject */}
                            <div style={{ flex: '1' }} className='add-main-row1-col1' >
                                <label>Select Subject</label><br />
                                <select className='add-select' name='subject'>
                                    <option value={'default'} disabled hidden>Search Subject...</option>
                                    {subAPI && subAPI.map((subject, i) => {
                                        return <option key={i} value={subject._id}>{subject.name}</option>
                                    })}
                                </select>
                            </div>
                            {/* for topic */}
                            <div style={{ flex: '1' }} className='add-main-row1-col1'>
                                <label>Select Topic</label><br />
                                <select className='add-select' name='topic' >
                                    <option value={'default'} disabled hidden>Search Topic...</option>
                                    {topicAPI && topicAPI.map((topic, i) => {
                                        return <option key={i} value={topic._id}>{topic.name}</option>
                                    })}
                                </select>
                            </div>
                            {
                            }       </div>
                        {/* for question type, level and marks */}
                        <div className='add-main-row'>
                            {/* for question type and level of difficulty */}
                            <div style={{ flex: '1' }} className='add-main-row2-col1' >
                                {/* for Question type */}
                                <div style={{ flex: '1' }} className='row2-div'>
                                    <label>Question Type</label><br />
                                    <select className='add-select' name='type' >
                                        <option value={'MULTIPLE CHOICE'}>Multiple Choice</option>
                                        <option value={'MULTIPLE RESPONSE'}>Multiple Response</option>
                                        <option value={'FILL IN BLANKS'}>Fill in the blanks</option>
                                    </select>
                                </div>
                                {/* for Difficulty Level */}
                                <div style={{ flex: '1' }} className='add-main-row2-half row2-div'>
                                    <label>Difficulty Level</label><br />
                                    <select className='add-select' name='diffLevel' >
                                        <option value={'easy'}>Easy</option>
                                        <option value={'medium'}>Medium</option>
                                        <option value={'hard'}>Hard</option>
                                    </select>
                                </div>
                            </div>
                            <div style={{ flex: '1' }} className='add-main-row2-col1 '>
                                <div style={{ flex: '1' }} >
                                    <label>Right Marks</label><br />
                                    <input type={'text'} name='rightMarks' className='add-input' />
                                </div>
                                <div style={{ flex: '1' }} className='add-main-row2-half'>
                                    <label>Wrong Marks</label>
                                    <input type={'text'} name='wrongMarks' className='add-input' />
                                </div>
                            </div>
                        </div>
                        <div className='questionText-div'>
                            <label>Question</label>
                            <textarea rows={5} style={{ width: '100%', borderRadius: '4px', border: '1px solid lightgrey' }} name='questionText' />
                        </div>
                        <div>

                            Options
                            {arr.map((i)=>{

                            })}
                            <div>
                                     <div className='options-row' >
                                            <div className='options-col left'>
                                                <input type={'radio'} name={'isCorrect'} id={`radio-1`} defaultValue={false} name='isCorrect' /> Option 1
                                            </div>
                                            <textarea rows={4}  id={`textarea-1`} className='options-col right' style={{ border: 'none', paddingLeft: '10px' }} name={`option`} defaultValue={''}  />
                                        </div>
                                        <button type='button' className='option-btm-btn' style={{ marginTop: '3px', marginRight: '10px', backgroundColor: 'white', border: 'none' }}> Remove Option</button>|
                                        <button type='button' className='option-btm-btn' style={{ marginTop: '3px', marginLeft: '10px', backgroundColor: 'white', border: 'none' }} >  Enable Rich Text Editor</button>
                                    </div>

                        </div>
                        <button type='button'>+Add Option</button>
                    </div>
                    <div className='add-footer'>
                        <button className='btn btn-primary' style={{ padding: '10px 15px 10px 15px', fontSize: '20px' }} type='submit'>Save question</button>
                        <button className='btn btn-light' style={{ marginLeft: '30px', fontSize: '20px', border: 'none', outline: 'none', padding: '6px 10px 6px 10px' }}>Cancel</button>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default AddQuestions
