import './App.css';
import React, { useEffect } from 'react';
import Main from './components/Main';
import Questions from './components/Questions';
import Subjects from './components/Subjects';
import Topics from './components/Topics';
import './components/Style.css';


import {
  BrowserRouter as Router,
  Routes,
  Route
} from 'react-router-dom';
import AddQuestions from './components/AddQuestions';
import EditQuestion from './components/EditQuestion';
import Login from './components/Login';

function App() {

  const [_activeUser,setActiveUser] = useState('')
useEffect(()=>{
  const temp = localStorage.getItem("_activeUser")
  setActiveUser(temp)
},[window.location.href])

  return (
    <div className="App">
      <Router>
      <div className='main-nav-bar'>
      <Routes>
      < Route path='/' element={<Login/>} />
      {window.location.href=="http://localhost:3005/" && _activeUser ?
          < Route path='/questions' element={<Questions/>} /> :  < Route path='/' element={<Login/>} />}
          < Route path='/subjects' element={<Subjects />} />
          < Route path='/topics' element={<Topics />} />
          < Route path='/add_question' element={<AddQuestions />} />
          < Route path='/edit/:Qid' element={<EditQuestion />} />
        </Routes>
      </div>
      </Router>
    </div>
  );
}

export default App;
